function Builder( emitter ) {
  
  emitter.on( 'namespace declare', function( name, block ) {
    emitter.emit( 'consume block' );
    console.log( block );
  } );

  emitter.on( 'definition', function( title ) {
    console.log( 'definition ' + title );
  } ); 

  emitter.on( 'declaration', function( title ) {
    console.log( 'declaration ' + title );
  } ); 

}

module.exports.Builder = Builder;