var commentSingle = new RegExp( '\/\/.*\n?', 'g' )
  , commentMultiple = new RegExp( '\/\\*.*?\\*\/', 'mg' )
  , preProDirectiveNewLine = new RegExp( '\\\s*?\n?', 'mg' )
  , preProDirectiveSingle = new RegExp( '#.*\n?', 'g' );
  

module.exports.stripPreProDirectives = function( code ) {
  code = code.replace( preProDirectiveNewLine, '' );
  code = code.replace( preProDirectiveSingle, '' );
  return code;
}
  
module.exports.stripComments = function( code ) {
  code = code.replace( commentSingle, '' );
  code = code.replace( commentMultiple, '' );
  return code;
}
